import React, { useState } from 'react';

import { Container, Row, Col } from 'react-bootstrap';

import '../components/SignupComponent'
import Header from '../components/Header';
import SearchFrontPage from '../components/SearchFrontPage'
import RightImageFrontPage from '../components/RightImageFrontPage'
import Head from 'next/head'
import { createGlobalStyle } from 'styled-components'
import styled from 'styled-components';
import 'bootstrap/dist/css/bootstrap.min.css';

const GlobalStyle = createGlobalStyle`
  p {
    font-family: "Roboto Light ,Times New Roman", Times, serif;
  }
`

// function Index() {
//   const [show, setShow] = useState(false);

//   const handleClose = () => setShow(false);
//   const handleShow = () => setShow(true);

//   return (
//     <>
//       <Button variant="primary" onClick={handleShow}>
//         Launch demo modal
//       </Button>

//       <Modal show={show} onHide={handleClose}>
//         <Modal.Header closeButton>
//           <Modal.Title>Modal heading</Modal.Title>
//         </Modal.Header>
//         <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
//         <Modal.Footer>
//           <Button variant="secondary" onClick={handleClose}>
//             Close
//           </Button>
//           <Button variant="primary" onClick={handleClose}>
//             Save Changes
//           </Button>
//         </Modal.Footer>
//       </Modal>
//     </>
//   );
// }



const Index = () => {  
  const [currentUser, setCurrentUser] = useState(false);
  const onClick = () => setCurrentUser('Marat');
  return (

    <Container>
      <GlobalStyle />
      <Head>
          <title>Stay in NY</title>
      </Head>
        <Row>
          <Header UserName={currentUser} onClick={onClick}/>
        </Row>    
        <FirstRow>
          <Col><SearchFrontPage/></Col>
          <Col><RightImageFrontPage/></Col>
        </FirstRow>
        <BottomRowTitle>
          <Col md={{ span: 4, offset: 4 }}><FeaturedSpacesTitle>Featured spaces</FeaturedSpacesTitle></Col>
        </BottomRowTitle>
        <BottomRow>
          <Col md={{ span: 12, offset: 0 }}>
            <FeaturedSpaces/>
          </Col>
        </BottomRow>
      </Container>
  );
}

const FirstRow = styled(Row)`
    background-image: url('/static/images/hompage_image_one.svg');
    background-size: cover;
    background-repeat: no-repeat;
    padding-bottom: 24%;
    padding-right: 0;
  `;

  const BottomRowTitle = styled(Row)`
    background-color: #ECECFA;
  `;

  const FeaturedSpacesTitle = styled.p`
    color: #A940A2;
    font-size: 2rem;
    text-align: center;
    margin-top: -50%
  `;

  const BottomRow = styled(Row)`
    background-color: #ECECFA;
    
  `;

  const FeaturedSpaces = styled.p`
    background-image: url('/static/images/rooms.svg');
    background-repeat: no-repeat;
    padding: 15%;
    background-position: center;  
    margin-top: -14%;
  `;

export default Index;
